package day04_arithmeticOperations;

public class average {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

		double num1=20, num2=3, num3=10, average;
		
		
		average=(num1+num2+num3)/3;
		
		System.out.println("Average of " + num1 +" and " +num2 +" and " + num3 + " is equal to " + average);
		

		double x=130;
		byte b= (byte) x;
		
		System.out.println(b);
		
		int pies=10, people=4;
		double piesPerson;
		piesPerson= (double) (pies/ people);
		
		System.out.println(piesPerson);
		
		
	}

}
