public class Task21_incrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			
			
			int x=4;
			
			int y= x*4 - x++;
			
			// x in memory : 4    - > 5
			// y result    : 4*4 - 4
			
			System.out.println(x);
			System.out.println(y);
		
	}

}
